sys_ui_page.client_script:
function onLoad() {
    var ga = new GlideAjax('PDFNameInserter'); // Call a Script Include
    ga.addParam('sys_id', 'sys_id_of_the_record'); // Replace with the actual sys_id
    ga.getXMLAnswer(processResponse);
}

function processResponse(response) {
    var result = JSON.parse(response);
    if (result.success) {
        var name1 = result.name01;
        var name2 = result.name02;

        // Assuming we have the PDF as a binary stream in the client
        var pdfStream = result.pdfBinaryStream; // This is the binary stream of the PDF
        var modifiedPdfStream = addNamesToPDF(pdfStream, name1, name2);

        // Now you can use this modified PDF stream as needed, e.g., download it or send it to the server
    } else {
        document.getElementById('canIconnect').innerText ='Error fetching data: ' + result.error;
    }
}

