sys_ws_operation.operation_script:
/*
To return a PDF instead of a JavaScript or CSS file, you'll need to 
adjust the setContentType method to specify that the content type 
is application/pdf. In addition, you'll want to write the content 
in a binary format rather than a string to ensure the PDF is 
correctly handled. 
*/

(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var fileName = request.pathParams.file;
    var currentRecord = "33f020641b8c1e1076f215ff034bcb21"; 
    var ar = new GlideRecord('sys_attachment');
    ar.addQuery('table_sys_id', currentRecord);
    ar.addQuery('file_name', fileName);
    ar.query();
    if (ar.next()) {
        var attachment = new GlideSysAttachment().getContentStream(ar);
        response.setContentType('application/pdf');
        response.setStatus(200);
        response.getStreamWriter().writeStream(attachment);
    } else {
        response.setStatus(404);
        response.getStreamWriter().writeString("file not found");
    }
})(request, response);
